schema:
{
    "info": {
        "title": "MinIO Object Manager",
        "description": "Manages CRUD operations on objects in a MinIO bucket.",
        "version": "v1.0.0"
    },
    "servers": [
        {
            "url": "http://localhost:9000"
        }
    ],
    "paths": {
        "/{bucketName}/{objectName}": {
            "put": {
                "description": "Uploads an object to the specified bucket.",
                "operationId": "uploadObject",
                "parameters": [
                    {
                        "name": "bucketName",
                        "in": "path",
                        "required": true,
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "objectName",
                        "in": "path",
                        "required": true,
                        "schema": {
                            "type": "string"
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Object uploaded successfully"
                    }
                }
            },
            "get": {
                "description": "Downloads an object from the specified bucket.",
                "operationId": "downloadObject",
                "parameters": [
                    {
                        "name": "bucketName",
                        "in": "path",
                        "required": true,
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "objectName",
                        "in": "path",
                        "required": true,
                        "schema": {
                            "type": "string"
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Object downloaded successfully",
                        "content": {
                            "application/octet-stream": {}
                        }
                    }
                }
            },
            "delete": {
                "description": "Deletes an object from the specified bucket.",
                "operationId": "deleteObject",
                "parameters": [
                    {
                        "name": "bucketName",
                        "in": "path",
                        "required": true,
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "objectName",
                        "in": "path",
                        "required": true,
                        "schema": {
                            "type": "string"
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Object deleted successfully"
                    }
                }
            }
        }
    },
    "components": {
        "schemas": {}
    }
}
